/*
 * Author: Mark Simpson (http://posterous.minimumchips.com/)
 * Version 0.2 (25th July 2009)
 * Hide banner ads and adjust page background styling.
 * Even if you already use ad blocking, this hides the big ugly spaces left behind.
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("content.cricinfo.com"),domain("www.cricinfo.com"),domain("stats.cricinfo.com") {

  html, body {
    background-position: center -100px !important;
  }

  /* Top banner ad */
  #ciHomeLeaderboard {
    visibility: hidden !important;
    height: 5px !important;
  }

  /* Main column banners */
  .seriesSpncr, .topFrameStripLHS {
    display: none !important;
  }

  /* RHS ads */
  #ciHomeContentrhs div.pnl320B, #ciHomeContentrhs div.pnl320M, #ciHomeContentrhs div#ciHomeSkybannerHolder, div#RHS2Adslot {
    display: none !important;
  }

  #ciHomeContentrhs div.pnl320T {
    visibility: hidden !important;
  }

  /* Bet365 ad */
  div#lvScrCnt div:first-child {
    padding: 0 !important;
  }

  div#lvScrCnt iframe {
    display: none !important;
  }

}
