============================================*/

@-moz-document domain("ogame.de") {

/*******************************
 ********filter the Ads*********
 *******************************/

/* block premium pictures */
  a[href*='micropayment'] {
    display: none ! important;
  }

/* block Dark Matter in header */
  table[id='resources'] td:first-child + td + td +td {
    display: none ! important;
  }

/* block Merchant */
  table[width='110'] tr:first-child + tr + tr + tr + tr {
    display: none ! important;
  }

/* block Board */
  table[width='110'] tr:first-child + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr {
    display: none ! important;
  }

/* block Officers Recruitment */
  table[width='110'] tr:first-child + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr {
    display: none ! important;
  }

/* block Help */
  table[width='110'] tr:first-child + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr {
    display: none ! important;
  }

/* 2. Balken ausblenden */
  table[width='110'] tr:first-child + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr {
    display: none ! important;
  }

/* Regeln ausblenden */
  table[width='110'] tr:first-child + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr {
    display: none ! important;
  }

/* Impressum ausblenden */
  table[width='110'] tr:first-child + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr {
    display: none ! important;
  }
}
