@-moz-document domain("open.spotify.com") {
/* --- QUICK FADE TRANSITION ANIMATIONS --- */

a._8e566af0d3a8e6834c933615bbb634ba-scss, button._8e566af0d3a8e6834c933615bbb634ba-scss, /* Right click list */
li[role="presentation"] > button, button[title="Play"], button[title="Pause"], /* Play - Pause buttons */
div[role="row"] > div[data-testid="tracklist-row"], /* All the different visual items from each track row */
div[role="row"] > div[data-testid="tracklist-row"] > div._7c993111016a33a158645119f8a6fb12-scss > div > button,
div[role="row"] > div[data-testid="tracklist-row"] > div._5845794624a406a62eb5b71d3d1c4d63-scss > div > span > *,
div[role="row"] > div[data-testid="tracklist-row"] > div.c6287512c50a737d01bd9db32b301fab-scss > a,
div[role="row"] > div[data-testid="tracklist-row"] > div.b9f411c6b990949776c8edf3daeb26ad-scss > div,
div[role="row"] > div[data-testid="tracklist-row"] > div.b9f411c6b990949776c8edf3daeb26ad-scss > *:last-child
{ transition: 0.15s; }

a.standalone-ellipsis-one-line._9a78420cc4a863b2f413ce55e759a321-scss, /* Left pane playlists */
li._0787ecf44d56591be72b1f2c0e2f2320-scss > a > span, /* Top library section buttons */
div > button._3e75c7f07bdce28b37b45a5cd74ff371-scss, /* User profile button */
button.ff6a86a966a265b5a51cf8e30c6c52f4-scss, /* Follow button */
button[title="Remove from Your Library"] > *, /* Unlike button */
button[title="Save to Your Library"] > *, /* Like button */
button[title="More"] > * /* More button */
{ transition: 0.2s; }

/* --- VISUAL TWEAKS --- */

div[role="row"][aria-selected="true"] > div[data-testid="tracklist-row"] { /* Darker selected tracks */
    background-color: hsla(0,0%,100%,.24);
}

div[role="row"] > div[data-testid="tracklist-row"] > div:last-child > button[title="Remove from Your Library"] > svg { /* Keep liked songs button green unless hovered (below) */
    color: #1db954;
}

button[title="Remove from Your Library"] > *:hover { /* Add red to unlike songs button */
    color: #ff3333 !important;
}

button[title="Save to Your Library"] > *:hover, button[title="More"] > *:hover { /* Add green to more menu */
    color: #1db954 !important;
}

button.ff6a86a966a265b5a51cf8e30c6c52f4-scss:hover { /* Follow button */
    cursor: pointer;
    border: 1px solid #1db954 !important;
}

button.ff6a86a966a265b5a51cf8e30c6c52f4-scss._888a8dffe06d27b161f0258c2769069e-scss:hover { /* Unfollow button */
    cursor: pointer;
    border: 1px solid #ff3333 !important;
}

a.standalone-ellipsis-one-line._9a78420cc4a863b2f413ce55e759a321-scss, ul._60cb742e518d084c3c959007b9463b51-scss > li > a, ul._60cb742e518d084c3c959007b9463b51-scss > li > button {
    cursor: pointer;
}

div.connect-device-list-content { /* Remove useless Spotify Connect scrollbar */
    overflow: hidden !important;
}

button._8e566af0d3a8e6834c933615bbb634ba-scss { /* Log out button padding */
    margin-top: 4px;
}

button.d2a8e42f26357f2d21c027f30d93fb64-scss { /* Songs right click menu visual tweaks */
    align-items: normal;
    color: hsla(0,0%,100%,0.75);
}

button[data-testid="top-bar-back-button"] > svg, button[data-testid="top-bar-forward-button"]:hover > svg { transition: 0.2s; }
button[data-testid="top-bar-back-button"]:hover > svg, button[data-testid="top-bar-forward-button"]:hover > svg { /* Back and forward buttons green on hover */
    color: #1db954 !important;
}

div._2f859138f9d0ecc3c687296f572c5dca-scss.bf37551e6974dfc6b7bc2307c4bc8d52-scss { /* Liked songs background in library */
    background: #181818;
}

button._3f37264be67c8f40fa9f76449afdb4bd-scss._4c3a5e6501aa956c9f1c5ad3e7286d6e-scss { /* Playlist titles border padding */
    border: 0px solid transparent;
}

a.c5a28c705b992b76c45dbb776cf76047-scss, a._2326696e1244e762bd7e426619b02370-scss { /* Search genre items */
    border-radius: 5px;
}

li._0787ecf44d56591be72b1f2c0e2f2320-scss:hover > a > span { /* Green hovering for Library sections */
    color: #1db954;
}
}
