@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("softoroom.net") {
table.logobg1, table.logobg2, table.logobg3, table.logobg4,
table table[align="center"][border="0"][cellpadding="0"][cellspacing="0"][height="100%"][width="100%"],
table[id='maincontent'] tr td[id='leftcolumn'],
table[id='maincontent'] tr td[id='rightcolumn'],
table[id="gfooter"],
div[class='copyright']
{display:none !important;}
div[class="borderwrap"] div[class='postcolor'] {display:block !important;}
table[id="userlinks"], table[id='maincontent'] table {
   width: 70% !important;
   margin-left: auto !important;
   margin-right: auto !important;}
}
