css:
@-moz-document domain("vk.com") {
/*панель слева*/
[dir] .side_bar_inner {
    padding: 15px 0 10px;
    background-color: rgba(125, 8, 255, .1);
     /*background: linear-gradient(to top,  rgba(161, 0, 255, .09),rgba(7, 0, 255, .09));*/
    margin-top: var(--header-height);
    opacity: 0.5;
}
[dir] .side_bar_inner:hover {
    padding: 15px 0 10px;
    background-color: rgba(1, 0, 255, .05);
    /*background: linear-gradient(to top,  rgba(161, 0, 255, .21),rgba(7, 0, 255, .17));*/
    margin-top: var(--header-height);
    opacity: 1;
}



/*цвет текста-ссылок слева*/
#side_bar ol li .left_row {
    color: #9198c1;
    display: -ms-flexbox;
    display: flex;
    white-space: nowrap;
    -ms-flex-align: center;
    align-items: center;
}
body.new_header_design #side_bar .left_label {
    color: #9198c1;
    font-size: 13px;
}



/*выделен прямоугольник*/
[dir] #side_bar ol li .left_row:hover {
    background: rgba(63, 0, 157, .5);
    border-radius: 2px;
}


/*каунтер, выделен прямоугольник*/
[dir] #side_bar_inner .left_row:hover .left_count_wrap {
    background-color: rgba(63, 0, 157, .5);
}
/*не выделен*/
[dir] .left_count_wrap {
    padding: 0 6px;
    background: rgba(102, 0, 255, .29);
    border-radius: 2px;
}
/*цвет цыфры*/
.left_count_wrap {
    --left-menu-counter-height: 28px;
    color: #c8c8c8;
    font-weight: 700;
    font-size: 11px;
    line-height: 17px;
}

/*заночилась*/


/*хидер*/
[dir="ltr"] #page_header_wrap {
    direction: ltr;
    /*background-color: rgb(99, 56, 153);*/
    background-color: #46489b;
}
body.new_header_design .top_nav_link {
    color: #fff;
}
[dir] body.new_header_design #page_header_cont {
    background-color: var(--white);
    border-bottom: 1px solid #007fff;
        border-bottom-color: #7b32e8;
        border-bottom-style: solid;
        border-bottom-width: 1px;
}
[dir] .TopHomeLink--logoWithText {
    background-image: none;
    background-size: auto 24px;
}
[dir] body.new_header_design .top_audio_player:hover {
    background-color: #ffffffbd;
}



/*трек*/
[dir] .page_block {
    background-color: rgba(255, 255, 255, 1);
    border-radius: 4px;
    box-shadow: 0 1px 0 0 var(--steel_gray_120),0 0 0 1px var(--steel_gray_80);
    margin: 15px 0 0;
}
/*название трека*/
.audio_page_player2 .audio_page_player_title_song {
    color: #000;;
    font-weight: 500;
}



/*главный фон*/
[dir] body {

    background: url('https://steamuserimages-a.akamaihd.net/ugc/941715990358314892/653B38BBFA3A62BB6E0B075D821AB0BB2ABE45B4/') 0% 100% no-repeat,
    url('https://steamuserimages-a.akamaihd.net/ugc/956347955212519811/9DDAA5B6646F8F9E608132C700F83FDBB6DC7FF1/?imw=512&amp;imh=230&amp;ima=fit&amp;impolicy=Letterbox&amp;imcolor=%23000000&amp;letterbox=true') no-repeat;
    background-attachment: fixed;

    background-position: -300px 0px, -200px -200px;
    background-color: #46489b;
    margin: 0;
    padding: 0;
}


/*вернуться наверх (панель слева появляется)*/
[dir] .stl_active.over_fast #stl_bg {
    background-color: rgba(50, 0, 111, .09);
    background: linear-gradient(to top,  rgba(161, 0, 255, .09),rgba(0, 0, 255, .09));
}


/*под хидером*/
[dir] .im-page_classic.im-page .im-page--header {
    margin-top: -63px;
    border-top: solid 15px rgba(0,0,0,0);
    border-bottom: none;
    border-radius: 0;
}
#ads_left {
    overflow: hidden !important;
    display: none !important;
}
}
