.tab,
.tab-container > div:hover {
    float: left;
    background-color: #3e3e3e;
    color: white!important;
    padding: 7px;
    border: 1px solid #9e9e9e;
    font-weight: bold;
    font-size: 15px;
    margin: 4px 2px 0 1px;
    border-bottom-width: 0;
    position: relative;
    top: -1px;
}

