css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("g2.botva.ru") {

div.mine table.items div.desc2 { 
    font-size:11px;
    height: 64px !important;
    padding-left:5px; 
}

body > table:first-child {
    background-color: gray;
}

body > table:first-child > tbody > tr > td:first-child {
    min-width: 0;
    width: auto;
}

body > table:first-child > tbody > tr > td:nth-child(2) {
    width: auto;
    min-width: 210px;
    text-align: right;
    background-repeat: no-repeat;
    background-position: right top;
}

body > table:first-child > tbody > tr > td:nth-child(3) {
    width: 574px;
    text-align: left;
}

body > table:first-child > tbody > tr > td:nth-child(4) {
    width: auto;
    min-width: 210px;
    text-align: left;
    background-repeat: no-repeat;
}

body > table:first-child > tbody > tr > td:last-child {
    min-width: 0;
    width: auto;
}

}
