css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#toolbar-menubar {

	min-height: inherit !important;

}



#menubar-items {

  	background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABQAAAAUAgMAAADw5/WeAAAACVBMVEUAAAD/+EkAAAD5/04nAAAAAXRSTlMAQObYZgAAAD1JREFUeF5lzDsRACAMA9BePWZAANyBCmwggaikkG5keEM/sT9OcpoDCAd3WFHCxnXFs8ucaKtLfWVDtn05Y3IbgaPy3CYAAAAASUVORK5CYII=) no-repeat left !important;

   margin: 0px 1px 0px 5px !important; 

	width: 20px !important;

}

#menubar-items:hover { 

 	background: none !important;

	width: auto !important; 

}

#main-menubar {

	visibility: collapse !important;

}

#main-menubar:hover, #menubar-items:hover > #main-menubar, menupopup { 

  	visibility: visible !important;



}

#file-menu, #edit-menu, #view-menu, #go-menu, #bookmarksMenu, #tools-menu, #helpMenu, #history-menu {

font-size: inherit !important;

}
