/*

T2 TRADEME TWEAK
www.trademe.co.nz

*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("trademe.co.nz"){

/* Main body tags */ 

.page-body{
width: 100% !important;
}

#mainContent{
width: 70% !important;
min-width: 750px !important;
}

.main{
width: 90% !important;
}

.ChangeRegionLink{
display: block !important;
position: absolute !important;
margin-top: -32px !important;
margin-left: 750px !important;
}

ul.login-area{
display: block !important;
margin-top: 100px !important;
margin-left: 500px !important;
z-index: 1000 !important;
}

.login-modal-box{
margin-left: 200px !important;
}

#site-nav{
margin-top: -27px !important;
}

.page-body{
border-top: 15px solid #FFF !important;
}

#footer{
display: none;
}

/* Logo */

.site-heading,.kevin{
height: 0px !important;
}  

/* Ad stuff */

#TradeMeAd_adIframe_LandingPageIsland, #FlashAds_Div, #divFlashAd, #divPL, #HomepageRight, #divMbAd, #HomepageAdSpace, #sideAd, #SiteHeader_SiteTabs_HeadingAd, #SiteHeader_SiteTabs_HeadingAdSpace{
display: none !important;
background: none !important;
visibility: hidden !important;
height: 0px !important;
}

}
