css:
/*The following three lines remove the dropmarker*/
#searchbar .searchbar-dropmarker-image {display: none !important;}
#searchbar .searchbar-engine-button {min-width: 22px !important; max-width: 22px !important;}
#searchbar .searchbar-engine-image {margin: 0 0 0 3px !important;}

/*Hide the "Choose search engine"-popup*/
.searchbar-popup {display: none !important;}

/*Disable highlighting*/
.searchbar-engine-button[addengines="true"] .searchbar-engine-button-top {
  list-style-image: url("chrome://browser/skin/Search-provider-bkgnd.png") !important;
  -moz-image-region: rect(0px, 32px, 10px, 0px) !important;
  height: 10px !important;
}

.searchbar-engine-button[addengines="true"] .searchbar-engine-button-mid-top {
  background-image: url("chrome://browser/skin/Search-provider-mid-top.png") !important;
}

.searchbar-engine-button[addengines="true"] .searchbar-engine-button-mid-bottom {
  background-image: url("chrome://browser/skin/Search-provider-mid-bottom.png") !important;
}

.searchbar-engine-button[addengines="true"] .searchbar-engine-button-bottom {
  list-style-image: url("chrome://browser/skin/Search-provider-bkgnd.png") !important;
  -moz-image-region: rect(12px, 32px, 22px, 0px) !important;
  height: 10px !important;
}
