@-moz-document url-prefix("https://vk.com/audios") {

/* Настройки для раздела "Музыка" */

/* Убрать пункты Верхнего меню в разделе Музыки */
#people_link_td,#communities_link_td,#games_link_td,#music_link_td.top_nav_link {display: none}

/* Ширина ТопАудиоплеера */
.top_audio_player {
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
    color: var(--light_blue_100);
    position: relative;
    max-width: 340px !important;
    height: var(--header-height);
    display: none;
    opacity: 0;
    pointer-events: none;}

/* Отступ Верхнего Аудиоплеера Слева и Справа */
[dir=ltr] .top_audio_player {
    padding-left: 10px !important; padding-right: 10px !important; }
}

