css:
@-moz-document url-prefix("https://www.brick-hill.com/") {
*
  {
  }

element.style {
    border-radius: 5px;
}
 body, html {
     background-image: url('https://cdn.discordapp.com/attachments/582605365145894922/800445405007315034/background.png');
     background-color: #4F89AE;
     background-repeat: no-repeat;
     background-position: top;
     margin: auto;
     margin-top: auto;
     width: 1200px;
     padding-top: 70px;
    
     }
nav {
    width:1904px;
    overflow:auto;
    margin:-230px 0 0 -352px;
}
body {
     background-color: rgb(255, 255, 255);
     background-image: url('https://cdn.discordapp.com/attachments/582605365145894922/800454687891390504/Banner.png');
     background-size: 100%;
     background-repeat: no-repeat;
     padding-top: 13%;
}
.main-holder {
    padding-top: 17%;
}

nav div.secondary {
    background-color:#212020;
    color: #fff;
    font-size: .6em;
}
nav .info {
    background-color:#212020;
}
.card .content {
    background-color:#FFFFFF
}
input, textarea {
    border:1px solid #C8C9C9;
    background-color:#fff;
}
.card {
    border-radius: 0px;
    box-shadow: 0 0px 0px rgba(255, 255, 255, 0);
    border:1px solid #C8C9C9;
}
.card .top:not(.absolute).blue {
    background: url(https://cdn.discordapp.com/attachments/582605365145894922/800450083559571486/bluebanner.png) round right top;
    height: 40px;
    color: #fff !important;
    font-weight: bold;
    font-size: 20px;
}
.card .top:not(.absolute).red {
    background: url(https://cdn.discordapp.com/attachments/582605365145894922/800450087175716904/redbanner.png) round right top;
    height: 40px;
    color: #fff !important;
    font-weight: bold;
    font-size: 20px;
}
.card .top:not(.absolute).green {
    background: url(https://cdn.discordapp.com/attachments/582605365145894922/800450084776575026/greenbanner.png) round right top;
    height: 40px;
    color: #fff !important;
    font-weight: bold;
    font-size: 20px;
}
.card .top:not(.absolute).orange {
    background: url(https://cdn.discordapp.com/attachments/582605365145894922/800453406787239946/orangebanner.png) round right top;
    height: 40px;
    color: #fff !important;
    font-weight: bold;
    font-size: 20px;
}
.card .top:not(.absolute) {
    background: url(https://cdn.discordapp.com/attachments/582605365145894922/800465598941364254/lightbluebanner.png) round right top;
    height: 40px;
    color: #fff !important;
    font-weight: bold;
    font-size: 20px;
}
button:not(.plain), .button:not(.plain) {
    border-radius: 0px;
}
button:not(.plain).blue, .button:not(.plain).blue {
    background: url(https://cdn.discordapp.com/attachments/582605365145894922/800450083559571486/bluebanner.png) no-repeat right top;
}
button:not(.plain).green, .button:not(.plain).green, button:not(.plain).bucks, .button:not(.plain).bucks {
    background: url(https://cdn.discordapp.com/attachments/582605365145894922/800450084776575026/greenbanner.png) no-repeat right top;
}
button:not(.plain).orange, .button:not(.plain).orange, button:not(.plain).bits, .button:not(.plain).bits {
    background: url(https://cdn.discordapp.com/attachments/582605365145894922/800453105145217054/orangebanner.png) no-repeat right top;
}
.box.item-img.owns, .box.item-img.special {
    border: 1px solid #C8C9C9;
}
.box {
    border-radius: 0px;
}
.owned-check {
    color: transparent;
}
.tabs .tab, .avatar-card.crate .img-holder, .profile-card {
    border-radius: 0px;
}
.side-ad {
    display:none;
}
.forum-tag {
    background: url(https://cdn.discordapp.com/attachments/582605365145894922/800466200446500864/forumtag.png)round right top;
}
.forum-tag::after {
    border-right-color: transparent;
 }
}
