css:
@-moz-document domain("ebay.co.uk"), domain("ebay.com"), domain("ebay.de"),domain("ebay.com.au"),domain("ebay.ca") {
 div[id*="v4-"] > div[id*="v4-"] > div[id*="v4-"].lview .divider.g-nav.g-b {
  display: none !important;
 }
 div[id*="v4-"] > div[id*="v4-"] > div[id*="v4-"].lview *.divider.g-nav.g-b ~ * {
  display: none !important;
 }
 div[id*="v4-"] > div[id*="v4-"] > div[id*="v4-"].lview .divider.g-nav:not(.g-b) {
  background-color: green !important;
 }
 div[id*="v4-"] > div[id*="v4-"] > div[id*="v4-"].lview *.divider.g-nav:not(.g-b) ~ * {
  display: block !important;
 }
 tr.li2hl td {
  background-color: inherit !important;
 }
 table.li2wb {
  border: none !important;
 }
}
