css:
@-moz-document domain("albicla.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 2px;
             background-color: #05090a;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
             background-color: #05090a;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 2px;
             -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .6);
             background-color: #3c4e71;
        }

body {
	background: #050505;
	color: #d1d5d9;
}
.checkbox {
	background: #0000;
}
.form-control {
    color: #b9cadb !important;
    background-color: #000 !important;
    border-color: #3e4042;
}
.form-label-group input:not(:placeholder-shown) ~ label {
	color: #888;
}
.text-muted {
    color: #9ca9b5!important;
}
.select2-container--default .select2-selection--single .select2-selection__rendered {
	color: #bbb;
}
.select2-container--default .select2-selection--single, .select2-dropdown {
	background: #000;
	border-color: #555;
}
.select2-container--default .select2-search--dropdown .select2-search__field {
	border-color: #555;
}
input {
	background: #000;
	color: #fff;
}
a:hover {
    color: #fafdff;
}
a {
    color: #75b5ff;
}
.alert-success {
    color: #6eff8f;
    background-color: #244a2d;
    border-color: #185526;
}
.alert-warning {
    color: #ffcc38;
    background-color: #1e1806;
    border-color: #2b2209;
}
main {
	background: #000205;
}
.card {
    background-color: #000;
    border-color: rgba(255, 255, 255, .125);
}
.bg-white {
    background-color: #000!important;
}
.border, .border-top, .border-bottom {
    border-color: #2e3033!important;
}
.nav-tabs .nav-item.show .nav-link, .nav-tabs .nav-link.active {
    color: #b9c8d7;
    background-color: #000;
    border-color: #2e3033 #2e3033 #000;
}
#avatar img {
	border-color: #000;
}
[src='https://albicla.com/imgcache/150x150/c/data/img/no-avatar.png'], [src='/imgcache/150x150/c/data/img/no-avatar.png'], [src='/imgcache/35x35/c/data/img/no-avatar.png'] {
	filter: invert(100);
	border-color: #fff !important;
}
#avatar {
	background: #000;
}
.input-group-text {
    color: #b9c8d7;
    background-color: #191c1f;
    border-color: #3e4042;
}
#avatar-edit button {
	border-color: #000;
}
.dropdown-item:focus, .dropdown-item:hover {
    color: #e6e8eb;
    background-color: #08090a;
}
.dropdown-menu {
    color: #d1d5d9;
    background-color: #000;
    border-color: rgba(255, 255, 255, .15);
}
.dropdown-item, .btn:hover, .btn {
	color: #d1d5d9;
}
header .input-group-navbar .btn, header .input-group-navbar .form-control {
    background: #03060b;
}
#timeline .card-body-item:hover {
	background: #111;
}
button.hover-blue:hover, button.hover-red:hover {
	background: #000;
}
.lightbox .lb-image {
	border-color: #000;
}
html {
    color: rgb(245, 245, 245);
}
.user-post a.mention, .official {
    color: #61a7f7;
}
hr {
	border-color: #222;
}
.lb-outerContainer {
	background: #000;
}
.tt-menu {
    background-color: #000;
    border-color: rgba(255, 255, 255, .2);
    -webkit-box-shadow: 0 5px 10px rgba(255, 255, 255, .2);
    -moz-box-shadow: 0 5px 10px rgba(255, 255, 255, .2);
    box-shadow: 0 5px 10px rgba(255, 255, 255, .2);
}
.lb-cancel, .lb-nav a.lb-prev, .lb-nav a.lb-next {
	filter: invert(100);
}
}
