css:
/* by storspov */
@-moz-document domain("likström.se") {
#status, #menu, #center .menu  {position:fixed!important;top:86px!important;}
#container, #top, #content, #center .menu {width:inherit!important;}
#center {position:relative!important;top:86px!important;z-index:1;margin-top:17px!important;}
#top {position:fixed!important;z-index:3;background:#383838;}
#center .menu {border-bottom:5px solid #383838;z-index:3}
#for-menu {position:fixed!important; }


.for-message.mark .message {border-top:109px solid #666!important;margin-top:-108px!important;z-index:1!important;position:relative;}
.for-message .message, #for-title {z-index:2!important;position:relative;}
.for-message {background:#555!important;z-index:2!important;position:relative;border-bottom:2px solid #555!important}
.for-message .message {margin-top:0!important}
.for-message.mark {z-index:1!important;position:relative;}
#for-title + .for-message.mark .message {border-width:1px!important;margin-top:0!important}
#for-title {outline:5px solid #555!important}
}
