css:
@-moz-document domain("kiezelpay.com") {
body {
    background-color:#18181a;
}

* {
    text-shadow: none !important;
}

.navbar {
    height:40px;
}

.header-logo {
    height:40px;   
    margin-top:5px;
}
.header-logo  img {
    height:40px !important;
}

.navbar-nav {
    padding-top: 2px;
}

#sticky-wrapper .mysecondnav,  .sticky-wrapper .is-sticky .mysecondnav{
    box-shadow: 0 0 12px 2px #6495ed !important;
    border-bottom:1px solid #6495ed !important;
    margin-top:-2px;
}


#sticky-wrapper .mysecondnav, 
#sticky-wrapper .mysecondnav .second-nav-item{
    transition: all 0.3s;
    background-color:#18181a !important;
    background:#18181a !important;
    color:white;
    border-top:1px solid transparent;
    border-bottom:none;
}

#sticky-wrapper .mysecondnav .second-nav-item:hover {
    transition:none;
    font-weight:bold;
}

#sticky-wrapper .mysecondnav .second-nav-item.item-active {
    color: #6495ed;
    font-size: 14px;
}


.footer {
    background-color: #1e1e1e;
    border-top:1px solid #6495ed;
}

.card {
    background-color: #1e1e1e;
}

.card  * {
    color:white !important;
    fill:#6495ed;
}

.card .c3-tooltip-container * {
    color:#6495ed !important;
    fill:#6495ed;
}

.c3 line, .c3 path {
	fill: none;
	stroke: #6495ed;
}

.c3-grid text {
	fill: #6495ed !important;
}

.c3-grid line {
	stroke: #6495ed !important;
}

.c3-tooltip {
	background-color: #1e1e1e !important;
    border: 1px solid #6495ed;
    box-shadow: 0 0 15px 1px #6495ed;
}

.c3-tooltip td {
    background-color: #1e1e1e !important;
	border-left: 1px dotted #999;
}

.selectionList {
	overflow: auto !important;
}


table thead tr th {
    font-weight:bold;
    background: #32373B;
}
table tbody tr td {
    background-color: #1e1e1e !important;
}

table tfoot tr th {
    font-weight:bold;
    background: #32373B;
}

.dataTables_wrapper .dataTables_paginate .paginate_button.current, .dataTables_wrapper .dataTables_paginate .paginate_button.current:hover {
	color: #6495ed !important;
    background: #32373B;
}

.dataTables_wrapper .dataTables_paginate .paginate_button {
	border-radius: 0;
}

.dataTables_wrapper .dataTables_paginate {
	padding-top: 0.55em;
    padding-bottom: 0.55em;
}

.dataTables_wrapper .dataTables_paginate .paginate_button.disabled, .dataTables_wrapper .dataTables_paginate .paginate_button.disabled:hover, .dataTables_wrapper .dataTables_paginate .paginate_button.disabled:active {
	color: #fff !important;
}

.selectionList input[type="checkbox"]:checked + label::before {
	background-color: #6495ed !important;
	border-color: #6495ed;
}

.materialInputGroup .bar::before {
	background: #6495ed !important;
}

.materialInputGroup .inputFilled ~ label {
	color: #82afff !important;
    font-size:13px !important;
}

.materialInputGroup input:focus ~ label {
    color: #82afff !important;
}

.pace .pace-progress {
	background: #82afff !important;
    box-shadow: 0 0 17px 3px #6495ed;
}

input:focus {
	border-bottom: 1px solid #6495ed !important;
}


.sectionTitle {
    font-size: 14px;
    color:#6495ed !important;
    max-width:93%;
}

.modal-dialog {
    top:25vh;
}

.modal-dialog .modal-content {
    background:#18181a !important;
    border: 1px solid #6495ed;
    box-shadow: 0 0 15px 1px #6495ed;
}

.modal-dialog .modal-content .modal-header {
    background: #32373B;
}

.modal-dialog .modal-content .modal-header button.close {
    background-color: transparent !important;
    border: none !important;
    color:#000 !important;
}

.modal-dialog .modal-content .modal-header button.close:hover {
    border:none !important;
    box-shadow:none;
}

.modal-dialog .modal-content .modal-body *, 
#generateShopModal *,
.modal-dialog .modal-content .modal-body .modalForm label{
    color:white !important;
}

#productDirectPurchasePageUrl {
    color:black !important;
}

.modal-dialog .modal-content .modal-body > label:nth-child(1),
.modal-dialog .modal-content .modal-body > label:nth-child(2),
.modal-dialog .modal-content .modal-body #fitbitLibSdkSelectionList .selectItem:nth-child(2),
.modal-dialog .modal-content .modal-body #fitbitLibSdkSelectionList .selectItem:nth-child(4)
{
    display:none;    
}

button:not(.outline-btn) {
    background-color: #6495ed !important;    
    color:#18181a !important;
    border: 1px solid transparent;
}

button:not(.outline-btn):hover {
    border: 1px solid #6495ed;
    box-shadow: 0 0 15px 1px #6495ed;
    font-weight:bold;
}

button:not(.outline-btn) .dropdownArrow {
    height:10px;
    margin-top:12px !important;
    margin-right:15px !important;
    filter: invert(6%) sepia(5%) saturate(851%) hue-rotate(1deg) brightness(5%) contrast(63%);
}

button.outline-btn {
    background-color:#18181a;
    border-color:#6495ed;
    color:#6495ed !important;
}

button.outline-btn:hover {
    background-color:#6495ed !important;
    color:#18181a !important;
}

button.outline-btn .dropdownArrow {
    height:10px;
    margin-top:12px !important;
    margin-right:15px !important;
    filter: invert(52%) sepia(95%) saturate(1149%) hue-rotate(195deg) brightness(96%) contrast(93%);
}

button.outline-btn:hover .dropdownArrow {
    filter: invert(6%) sepia(5%) saturate(851%) hue-rotate(1deg) brightness(5%) contrast(63%);
}

.productRow {
    background-color: #1e1e1e;
    border: 1px solid #32373B;
    height:auto;
}

.productRow:hover {
    border: 1px solid #6495ed;
    box-shadow: 0 0 12px 1px #6495ed;
}

.productRow  p {
    color:white !important;
}

#products-container, #bundles-container {
    display:flex;
    flex-wrap: wrap;
    justify-content:center;
}

#products-container .productContainer,
#bundles-container .productContainer{
    margin: 14px 9px;
    background-color: #1e1e1e;
}

.productContainer .productRow .imageContainer {
    margin-left:auto;
    margin-right:auto;
}
.productContainer .productRow .imageContainer .secondProductImg {
    display:none;
}
.productContainer .productRow .imageContainer .firstProductImg {
    position:relative;
    background-color:transparent;
    box-shadow:none;
    max-height:150px;
}

.productContainer .productRow .imageContainer .firstProductImg img {
    max-height:150px;
    vertical-align:middle;
}

.productContainer .productRow .productDetails {
    margin-top: 19px;    
    text-align:center;
}

.productContainer .productRow .productDetails .productTitle {
    font-weight:bold;
}


.productContainer .productRow .rightSection .secondColumn {
    padding-top:10px;
}
.productContainer .productRow .rightSection .thirdColumn {
    padding-top: 5px;
}
.productContainer .productRow .rightSection .fourthColumn {
    padding-top:5px;
}

.productContainer .productRow .rightSection .fourthColumn .btn-group {
    margin-right:10px;
    margin-left:10px;
    margin-bottom:10px;
    max-height:40px;
}

.activeBundle {
    background-color: #6495ed;
}

.dropdown-menu, .dropdown-menu .inActiveBundle {
    margin-top:3px;   
    background-color: #1e1e1e !important;
    border: 1px solid #6495ed !important;
    box-shadow: 0 0 12px 1px #6495ed;
    padding:0;
    width:auto;
    max-height:500px;
    overflow-y:auto;
    min-width:175px;
}

.productDetails .dropdown-menu {
    margin-top: -144px;
	margin-left: -28px;
}
.dropdown-menu li a {
    color: #6495ed !important;
    padding: 10px;
    border-radius:0 !important;
    
}

.dropdown-menu li:hover a {
    background-color:#6495ed !important;
    color:#18181a !important;
    box-shadow: 0 0 12px 1px #6495ed;
}


.dropdown-menu .inActiveBundle {
    padding:10px;
}

.dropdown-menu .activeBundle {
    color:white !important;
}

#bundles .dropdown-menu {
    border:none !important;
    box-shadow: none;
}

.dropdown-menu li:hover .activeBundle[data-product-action="activate"] {
    border:1px solid #04A764 !important;
    background:#04A764 !important;
    box-shadow: 0 0 12px 7px #04A764 !important;
}

.activeBundle {
    padding:7px;
}

.inActiveBundle {
    padding: 7px;
    color:black !important;
}

#products-container .productContainer .productRow *, #bundles-container .productContainer .productRow * {
    float:none !important;
    width:auto;
    color:black;
}


.slideModalBg {
	background: rgba(36, 36, 36, 0.7);
}

.slideModal {
    border-right: 2px solid #6495ed !important;
    box-shadow: 0 0 15px 5px #6495ed;
}

.slideModalHeader {
    background: #32373B;
    border-bottom: 1px solid #6495ed !important;
    height: 91px;
    text-align:center;
    width: 418px !important;
}

.slideModalHeader .slideModalTitle {
    font-size:35px;
    padding-left:0;
    padding-top:26px;
}

.slideModalContent {
    background: #1e1e1e;
    padding-top:10px;
}

.slideModalContent * {
    color:white !important;
}

.slideModalContent .selectionList .selectItem label {
    color:white !important;    
}

/* .slideModalContent .materialInputGroup:nth-child(14) */

.slideModalContent .pick-platform-accordion-container:nth-child(2),
.slideModalContent .pick-platform-accordion-container:nth-child(3),
.slideModalContent .materialInputGroup:nth-child(8),
.slideModalContent .materialInputGroup:nth-child(10),
.slideModalContent div.materialInputGroup[data-toggle],
.slideModalContent #garmin-image-upload-form,
.slideModalContent #pebble-image-upload-form,
.slideModalContent > p {
    display:none !important;
}

.slideModalContent #fitbit-image-upload-form {
    margin-left:60px;
    margin-bottom:15px;
}
.slideModalContent #fitbit-image-upload-form #fitbit-upload-container {
    width: 200px;
    height: 200px;
}

.slideModalContent .form-check label {
    font-size:8px !important;
}

#info-container .card.stat * {
    color:white;
}
#info-container .card.stat {
    background: #1e1e1e;
    border: 1px solid #6495ed !important;
    box-shadow: 0 0 12px 1px #6495ed;
}

#info-container .card.stat .stat-header {
    border-top: 3px solid #6495ed;
}
}
