css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kffl.com") {
  div[id^=mainnav],
  div[id^=login],
  div[id^=footer],
  div[id^=clearer],
  div[class^=onethirdboxred],
  div[class^=onethirdboxsilver],
  div[class^=onethirdboxwhite],
  div[class^=onethirdboxblank],
  p[id^=mvp],
  #main>table>tbody>tr>td[style="padding: 10px 0pt 0pt 6px;"],
  [src*="2mdn.net"],[href*="2mdn.net"],
  [src*="doubleclick.net"],[href*="doubleclick.net"],
  iframe[src*="fantasyplayers.com"]

  {display: none !important;}

  div[class^=enclosure] { width: 100% !important; }
  table#top { background:#000000; }

}
