css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

window #urlbar {margin-right: 0px !important; border-top-right-radius:0!important; border-bottom-right-radius:0!important;}
#search-container .searchbar-textbox {border-top-left-radius:0!important; border-bottom-left-radius:0!important;}
window #urlbar-search-splitter:not(:hover) + #search-container .searchbar-textbox {margin-left: 0px !important; border-left:0 !important;}
window #urlbar-search-splitter:hover {width:12px !important;}
window #urlbar-search-splitter {
width:3px !important;-moz-transition:width ease .06s !important;
opacity:.1!important; min-width: 1px !important; margin: 0 -1px 0 -2px !important;}
#urlbar .chromeclass-toolbar-additional {border-radius: 0 !important;}
