@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.godofwar.com") {

/*
 * GodofWar.com Community: No Times Roman
 *
 * Version: 1.0
 * Author: Xuchilbara
 * http://forums.godofwar.com/t5/user/viewprofilepage/user-id/49
 * --------------------------------------------------------------
 * Do feel free to modify and improve upon this work. I don't
 * mind as long as: i) It is done to improve the godofwar.com
 * community. ii) Credit is given to me, Xuchilbara. ^_~
 */
   
  /* addressing all headers*/

   h1, h2, h3 {
               font-style: normal; 
               font-family: Tahoma !important; 
               font-variant: small-caps;
              }

  /* addressing general body text*/ 

          div {
               font-size: 9pt;
               font-family: Tahoma !important;
              }

  /* addressing font on the board user menu line and tags */

           li {
               font-size: small !important;
               font-family: Tahoma !important;
              }

}
