css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.tumblr.com") {
    body#dashboard_index li[id^="post"] img.image {
        opacity:0.05;
    }
    body#dashboard_index li[id^="post"] img.image:hover {
        opacity:0.30;
    }
    body#dashboard_edit_post div#left_column img {
        opacity:0.05;
    }
    body#dashboard_edit_post div#left_column img:hover {
        opacity:0.30;
    }
    body#dashboard_drafts div#left_column img {
        opacity:0.05;
    }
    body#dashboard_drafts div#left_column img:hover {
        opacity:0.30;
    }
    body#dashboard_post_queue div#left_column img {
        opacity:0.05;
    }
    body#dashboard_post_queue div#left_column img:hover {
        opacity:0.30;
    }
    div#tag_editor {
        height: 250px !important;
    }
}
