css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("de.wikipedia.org")
{
#p-search
{
position:fixed !important;
z-index:9999 !important;

padding-bottom:1em !important;
}

.portlet:not(#p-personal):not(#p-cactions)
{
padding-top:2px !important;
}

.portlet:not(#p-personal):not(#p-cactions) h5 
{

background-color:white !important;
border:1px solid #AAAAAA !important;
margin-bottom:-1px !important;
border-bottom:1px solid #eee!important;

margin-left:0.5em !important;
padding:0 0.5em !important;
-moz-border-radius-topleft:5px !important;
-moz-border-radius-topright:5px !important;

border-top: 2px solid #aaa !important;
-moz-border-top-colors: #aaa #eee !important;
border-left: 2px solid #aaa !important;
-moz-border-left-colors: #aaa #eee !important;
border-right: 2px solid #aaa !important;
-moz-border-right-colors: #aaa #eee !important;

}
*:not(#p-personal):not(#p-cactions) > .pBody
{
-moz-border-radius-bottomright:5px !important;
-moz-border-radius-topright:5px !important;

border-bottom: 3px solid #aaa !important;
-moz-border-bottom-colors: #aaa #ddd #eee !important;
border-right: 3px solid #aaa !important;
-moz-border-right-colors: #aaa #ddd #eee !important;
border-top: 3px solid #aaa !important;
-moz-border-top-colors: #aaa #ddd #eee !important;
}
#p-navigation
{
margin-top:6.5em !important;
}
input[type="submit"]
{
border:1px solid #AAAAAA !important;
background-color:#FFFFFF !important;
border-bottom-width:2px !important;
margin-bottom:2px !important;
margin-top:2px !important;
padding:1px 3px !important;


}
}
