css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("comics.com") {

.HDR_ComicsLink, .HDR_PigsLink, .HDR_LinkSearchFAQ,
.Column_A, .Col_A_Footer, .Col_B, .Promo_1a,
.AD_Property, .AD_FooterAdsTitle, .Ad_B_Wrapper,
.Ad_C {display:none !important}

body, .Content, .Footer, .HDR_Nav_Wrapper, .HDR_Nav, 
.SRCH_Header, .STR_Header, .STR_Footer, 
.STR_Comic {background:none !important}

.HDR_Nav_Wrapper, .HDR_SearchInput, .HDR_Nav > ul,
.Col_A {margin:0 !important}

.Col_A {padding-top:0 !important}

.Header, .Content, .Footer, .HDR_Nav_Wrapper, .HDR_Nav {
  width:auto !important}

.HDR_Link {height:52px !important}
.HDR_Nav_Wrapper {height:64px !important}

.HDR_Login {
  position:absolute; top:0; right:0; z-index:9 !important;
  margin:0 !important}

.FAQLink:before, .SearchLink:before, .SRCH_AddComic:before, 
.STR_AddComic:before {content:attr(title)}

.HDR_Nav {height:auto !important; margin:-12px 0 0 !important}

.HDR_SearchAllComicsLink,
.HDR_SearchGo, .HDR_SearchInput,
.HDR_Nav > form {
  position:static !important; display:inline-block !important;
  width:auto !important; height:auto !important; vertical-align:middle;
  clear:both}

.SCH_Wrapper {top:56px !important; left:8px !important}

.STR_Container {margin-top:4px !important}

}
