css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("mydigitallife.info") {

body>div#errorPageContainer,
#noindent>ul>li,
#content>small,
.cse-branding-logo,
#footer,
#search_terms,
.cse-branding-right,
#search,
#noindent2>ul>li>ul>li>ul>li,
#content>div.post>div.entrytext>p.postmetadata,
#noindent2>ul>li>h2,
center>center,
.adspot,
.post>center>center>a>img,
#content>center>center>a>img,
.entrytext>center>center>a>img,
iframe,
.inlineadsense,ins,
.alt,#aus *,.nobrd,#google_flash_inline_div,#ads *,div .al *,.alt,#taw1 *,
.topadsense,
span.kLink,
div .post > center,
#content>center>div,
div#sidebar,
.entrytext > p > a > img,
div#content > center > a > img[width="300"][height="250"],
div#content > br
  {display:none !important;}

#page{width:920px !important; padding-top:0 !important;}
#header{width:100% !important; height: 60px !important;}
#header{padding-bottom:10px !important;}
.page_item>a{font-weight:bold;line-height:25px !important;}
.current_page_item{line-height:normal!important;}
.current_page_item>a{padding-bottom:2px!important;}
div#content {width:700px !important;padding:4px 15px !important;}
#content>div.post{width:700px !important;}
div.entry{padding:0 5px 0 0 !important;}
div#sidebar2{width:185px !important;background-color:#b3d0e8 !important;}
div#sidebar2 a:link, div#sidebar2 a:visited {color:#444 !important;font-weight:normal !important;font-style:italic !important;}
/*#content>div.post>div.entry>p{visibility:hidden !important;}*/
div#noindent2 { margin:0 !important; }
#noindent2 li{margin:0 0 20px -5px !important;}
ul.menu { top: 35px !important; left: 450px !important; }

}
