css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikipedia.org") {
  #globalWrapper {
      max-width: 990px;
      margin: auto !important;
  }
  #column-one { padding-top: 0 !important; }
  #p-cactions {
    left: auto !important; 
    margin: 0 0 0 11.5em !important;
    max-width: 990px;
  }
  #p-personal { 
    position: relative !important;
    max-width:990px !important;
    overflow: hidden !important;
  }
  #p-personal ul { padding-right: 0 !important; }
  #p-logo { position: relative !important; top: -1.5em !important; }
  #content { border-right: 1px solid #AAAAAA !important; }
}
