css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bash.org.ru") {

#page {
 width: 100% !important;
}
div.q {
 padding: 10px !important;
}
div.q div {
 font-family: Verdana !important;
 font-size: 11pt !important;
}
.menublock {
 float: left !important;
 width: 100% !important;
}
.searchblock {
 float: right;
 margin-right: 10px;
}
.searchblock .searchbox {
 margin-right: 10px;
}
#bar, .news, #page small, .badges {
 display: none;
}
.header, .menu {
 margin: 0 !important;
 padding: 0 10px;
}
#quotes {
 padding-bottom: 30px;
}

}
