css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("shuangtv.com") {
  body,
  div#container {
    background: #336699 !important;
    color: #336699 !important;
  }
  a, a:link, a:visited {
    color: #336699 !important;
  }
  a:hover {
    color: #FF0000 !important;
  }
  .content-width {
    width: 80% !important;
    font-size: 120% !important;
  }
  div#mainContent {
    background: white none !important;
  }
  div#innermainContent {
    margin-right:0px !important;
    width:100% !important;
  }
  #FlashH {
    height:0 !important;
  }
  p,
  div[align="center"],
  a[target="_blank"],
  hr,
  font[color="red"],
  div.Content-Info,
  div#FlashHead,
  div#Content_CCVideoForPJBlog,
  div#Content_shuoming,
  div#Content_Notice,
  div#MsgContent,
  div#mainContent-topimg,
  div#mainContent-bottomimg,
  div#sidebar,
  div.sidepanel,
  div#header,
  div#info,
  iframe {
    display:none !important;
  }
}
