css:
@-moz-document domain("japan.cnet.com") {
 body {
   min-width: 0px !important;
 }

 div.block_large_top,
 div.ad_text_bottom,
 div.block_paper,
 div.block_license,
 div.block_induction_bnet,
 div.ad_rect_bottom,
 div.ad_text_top,
 div.block_powercenter_linkbox {
  display: none !important;
 } 

 *,
 div.site_frame,
 div.frame_col_left,
 div.frame_col_right,
 div.block_blog_recent {
  width: auto !important;
 }

 div.site_body {
  margin: 0px 30px !important;
 }

 div.col_300,
 div.col_160,
 div.blog_item {
  width: 48% !important;
 }

 .site_logo a {
   width: 294px !important;
 }
}
