css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mozilla-remix.seesaa.net") {
  #content center,
  #content iframe,
  #box iframe,
  #box center,
  div.AMN-large-banner,
  #content > br,
  #links-left > br,
  #links-right > br,
  #content > ins {
    display: none !important;
  }

  #links-right-box, #links-left-box {
    display: none;
  }

  #content {
    width: 100% !important;
  }

  #banner {
    padding-bottom: 0 !important;
  }
}
