@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mangafox.com") {
#bottom_ads { display: none !important; }
#footer { display: none !important; }
#topad { display: none !important; }
#top_center_bar, #top_left_bar, #top_right_bar { height: 50px !important; }
.clear .gap { display: none !important; }
.page #bottom_center_bar { display: none !important; }
.page #bottom_left_bar { display: none !important; }
.page #bottom_right_bar { display: none !important; }

#advertisment { display: none !important; }
.ad728x90 { display: none !important; }
.clear > .right { display: none !important; }

[style^="background:url(/media"] { display: none !important; }
}
