css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hzqtcblog.appspot.com") {

body {
  background-color: #051e21 !important;
  padding: 0; margin: 0;
}

#banner {
  padding: 10px;
  background-color: #fff !important;
}

#container {
  background-color: #fff;
  padding: 50px;
  width: 650px !important;
}

h1 {
  padding-top: 20px;

}
h1 a {
  font-size: 400% !important;
}
h2 {
  font-family: "Envy Code R", Courier !important;
  font-size: 150% !important;
  background-color: #856434;
  border-left: 60px solid #f28c28;
  padding-left: 10px;
  margin: 0 -60px !important;
}

img {
  max-width: 600px;
  border: 1px solid #dbdbdb !important;
  padding: 2.5px;
}

#articleLeft {
  width: 480px !important;
  margin: 0 !important;
}

#articleRight {
  width: 150px !important;
  margin: 0 !important;
}

.articletitle, .articletitle a {
  color: #856434 !important;
  font-size: 120% !important;
  font-family: "Microsoft Yahei", Arial !important;
}

.categoryitem {
  margin: 0 !important;
}

.forquote {
  border: none !important;
  background-color: #fff9b5 !important;
  padding: 10px 20px !important;
  color: #00302d;
}

}
