css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.jira.xyz'){

body, body * {
font-family:Arial !important; 
color:#000 !important;
font-size:8pt !important;
line-height:10pt !important;
font-stretch:narrower !important;
}

#header-top, #header-top *,
.tableUnderline, .tableUnderline *,
#filterFormHeader, #filterFormHeader *, 
.leftFormHeader, .leftFormHeader *
{
background-color:#DDD!important;
border-bottom:none !important;
}

.filterSummaryCell {
background-color:#FFF !important;
}

#filterForm img[src="/images/border/spacer.gif"] {
display: none;
}

#menu, #menu * {
background-color:#DDD!important;
}

a {
color:#33C !important;
text-decoration:none !important;
}
a:hover, a:hover *{
color:#00F !important;
text-decoration:underline !important;
text-shadow: #AAA 1px 1px 2px;
}

}
