css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikipedia.org") {

/* no frindge, header, footer */
div > [id="siteNotice"],
div > [id="column-one"],
div > [id="footer"]
 {
  display: none !important
 }
#content 
 {
  margin: 0 0 0 0 !important;
  border-style: none !important;
 }







/*Colors*/


*{
 background-color: black !important;
 color: grey !important;
}

/* article body */
a:link{color: #4FAF4B !important;}
a:visited{color: #4B50AF !important; }

/* non existing pages */
a.new:link, a.new:visited{color: #B78464 !important;}



/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
/* coloring classes */
h1, h2, h3, h4, h5, h6 { color: #CABB51 !important;}
table.wikitable, table.wikitable *{background-color: #111111 !important; border-color: #57220f !important; color: #eeeeee !important;}
/* the content-Box */
.toctext, .tocnumber { color: #4FAF4B !important; }



}
