css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lifehacker.com"), 
               domain("io9.com"), 
               domain("gizmodo.com") {
}

@media print {

#navigation, 
#switch_blogview, 
.gnc_external, 
#rightwrapper, 
.post-supp *, 
.related-heading, 
.related, 
#comments, 
.sitescontainer, 
.ad_perma-panorama, 
#sitetags { 
    display: none !important; }

.post-meta, 
div.line { 
    background: none !important; 
    display: block !important; }

div.line cite, 
div.line span, 
a.author {
    display: inline !important; }

.post-supp { 
    float: none !important; 
    position: absolute !important; 
    top: -50px !important; 
    margin-top: 0 !important; 
    right: 0 !important; }

.post-meta { 
    height: auto !important; }

.inner { 
    padding-bottom: 0 !important; }

#main-container { 
    width: 90% !important; }

#main-container > .inner, 
.topchrome, 
#container, 
.gmgrid, 
.gmgrid .grid-full {
    width: 100% !important; }

}
