css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://de.netlog.com/") {

    /* hide bottom line & text on startpage */
    .content hr,
    p.dimmed { display: none !important }

}

@-moz-document domain(netlog.com) {

    /* hide google search & friend invite */
    form.contentSideBox,
    #friendInviteTrigger,

    /* hide beta badges */
    span.betaBadge,

    /* hide news, brands, polls & contests */
    .PersonalHomePageNewsComponent,
    .PersonalHomePagePagesComponent,
    .PersonalHomePageFeaturedPollComponent
    .PersonalHomePageContestsComponent,

    /* hide footer */
    #footer,

    /* hide ads */
    .ad { display: none !important }

}
