@-moz-document domain("alfalfa.livedoor.biz") {

* {
  margin: 0 !important;
  padding: 0 !important;
  font-size: 1em !important;
  background-image: none !important;
}

iframe,
noscript,
#left,
#right,
#banner .b2,
#banner .description,
#container .posted3,
#container .posted4,
.fullbody>.main,
.fullbody .keyword,
#articlebody>.blogbody>.an,
#articlebody>.blogbody>.an~*,
#articlebody>.blogbody>script[src*="/kanren/"]+br~*,
#blogcontainer~*,
#wrapper~*,
.blogbody>.main>.emore~*
  { display: none !important; }

body
  { background-color: #f2e8c6 !important;}

#content,
#blog,
#blog .fullbody
  { background-color: transparent !important; }

.blogbodybottom
  { border-bottom: none !important; height: 15px !important; }

#banner,
#container,
#blogcontainer,
#wrapper,
#content,
ul.article-pager
  { width: auto !important; height: auto !important; }

#container,
#articlebody>.blogbody,
.blogbody>.main
  { padding: 0.5em !important; }

ul.article-pager li
  { font-size: small !important; width: 45% !important; padding: 0 0.5em !important; }

}
