css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(forums.winamp.com) {

* {
  border: 0 !important;
  background-color: Window !important;
  color: WindowText !important;
}

#container {
  width: 90% !important;
  padding: 5px !important;
  -moz-border-radius: 10px !important;
  border: 1px solid WindowFrame !important;
}

#subnav, #pageheader, #content {width: 100% !important;}

body {background: AppWorkspace none !important;}

html {background: AppWorkspace none !important;}

table, td, tr, div {
  border: 0 !important;
  background-image: none !important;
}

h1, h2, h3 {font-size: 1em !important;}

h1 {background-color: Window !important;}

a {
  font-weight: bold !important;
  text-decoration: underline !important;
  background-image: none !important;
}

td {background-color: Window !important;}

a:hover {text-decoration: none !important;}

#nav ul li {background-image: none !important;}

#nav ul li.active {background-color: Window !important;}

a:hover img:hover {border: 0 !important;}

table#ltlink {border: 0 !important;}

#subnav ul {font-size: 1em !important;}

b {color: WindowText !important;}

a:hover img {border: 0 !important;}

tr td > font font {color: WindowText !important;}

td > b > font {color: WindowText !important;}

table table tr td > font {
  color: WindowText !important; 
  font-weight: bold;
}

hr {background-color: WindowText !important;}

}
