@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wagn.org") {

/*

  NAME: Blue Wagn (for use on wagn.org to change the green theme to corresponding blue)

  NOTE: The color values are in percentages to make it easier to adjust the scripts.

*/

/* top menu bar background */
#top-menu { background: rgb(0%, 0%, 35%) !important}

/* remaining background */
body.wagn { background: rgb(73%, 80%, 87%) !important}

/* background for cards */
.card-slot .card-header { background: rgb(0%, 0%, 35%) !important}

/* links */
a, a:link, a:visited { color: rgb(27%, 27%, 53%) !important} 

/* special for just the home  page */
.w-home-main h1 a, .cardid-6067 h1 a { color: rgb(27%, 27%, 53%) !important; }

/* same as original wagn.org, but required to override the links usage from above */
.card-slot .card-header, .card-slot .card-header a, .card-slot .card-header .title a, .card-slot .card-header .title a:link, .card-slot .card-header .title a:visited, .card-slot .card-header .title a:hover {
color: #FFFFFF !important;
}

/* another override */
#top-menu a {color:white  !important;
}

/* special for home page */
.w-home-main em, .cardid-6067 em {color: rgb(27%, 27%, 40%) !important;}

/* heading on home page */
body.wagn .w-home-main h1, body.wagn .cardid-6067 h1 {color: rgb(0%, 53%, 0%) !important}
 
}
