css:
@-moz-document url-prefix(http://www.aussiefrogs.com) {

[background="images/styles/frogstyle/misc/mainbg.jpg"] { display:none !important; }
body {background: white !important;}
.thead {background: #999 none !important;}
.alt2 {background: #ccc none !important;}
[class="alt1"] a:visited {color:purple !important;}
.alt1, .alt1Active {
background-color: #E8E9EB; background-image:none !important;
border-bottom: 1px solid #999 !important;	
}
.alt2, .alt2Active {
background-color: #C1DFC3; background-image:none !important;
border-bottom:1px solid #666 !important;	
}
.tcat, .tca1, .thead, .tfoot, .button {background-image:none !important;}

}
