@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("boingboing.net") {
 .bucket           {background-color:#333333!important;}
 .bucket-alt       {background-color:#999999 !important;}
 .bucket-short     {background-color:#333333!important;}
 .entry-author a   {color:#3399FF!important;}
 .asset-meta       {color:#3399FF!important;}
 .entry-title a:hover, .entry-snippet-title a:hover 
                   {color:#3399FF!important;}
 .asset-body a:hover, .asset-body a 
                   {color:#3399FF!important;}
.metabig a         {color:#3399FF!important;}
.meta a            {color:#3399FF!important;}
.comments-header a {color:#3399FF!important;}
.separator  {color:#3399FF!important;}

}
