css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {
   div.tab,#side{-moz-border-radius: 0.8em !important;}
   #footer,#navigation{-moz-border-radius: 0.4em !important;padding:10px !important}

   #side div.msg + ul{display:none}
   
   div.tab tr td+td {font-size:13px !important}

   tr.odd{ -moz-outline: 1px solid #C0C0C0 !important;-moz-outline-radius: 0.4em !important;color:#777 !important}
   tr.odd:hover{ -moz-outline: 1px solid #555 !important;-moz-outline-radius: 0.4em !important;color:black !important}

   tr.even{background-color:#eeFFdd !important;}
   
   a:hover{text-decoration:none !important}

   td.thumb a img{border:1px black solid !important;-moz-border-radius:0.3em !important;padding:2px !important;background-color:#fff !important;}
   td.thumb a:hover img{border:1px green solid !important}
   td.thumb+td strong > a{color:#333399 !important;font-weight:normal !important}

   span.meta{font-size:10px !important;color:#777 !important;display:block !important;margin-top:14px !important;text-align:right !important;}
   span.meta a{font-size:10px !important}

#friends a[rel=contact] { display:block !important; text-decoration:none!important; margin:0 0 8px 0!important; }
#friends a[rel=contact] img { margin:0 4px -8px 0!important; text-decoration:none!important; border:1px solid #999!important;}
#friends a[rel=contact]:after { content: attr(title) !important; font-family:Verdana !important; font-size:.9em !important; }
#friends a[rel=contact]:hover{text-decoration:underline !important}
#friends{height:500px !important;overflow:-moz-scrollbars-vertical !important}
}
