css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {
/* advertisement is most background */
div[id*="ad"]
{
 position:relative; z-index:0 !important; 
}

/* screenshot is 2nd */
 img[class*="screenshot"]
{
 position:relative;
 z-index:1 !important; 
}

/* view code is 2nd */
pre#view-code
{
 position:relative;
 z-index:1 !important;
}

/* install block is most foreground */
div#install
{
 position:relative;
 z-index:99 !important;
}

}
