css:
@-moz-document domain(p2pnet.net) {


body
{
    font-family: "DejaVu LGC Sans", "DejaVu Sans", sans-serif !important;
}


h1, h2, h3, p, a, th, td, ul, ol, li, dl, dd, dt, label, div, span, input, textarea
{
    font-family: "DejaVu LGC Sans", "DejaVu Sans", sans-serif !important;
}

td.newslist {
	font-family: "DejaVu LGC Sans", "DejaVu Sans", sans-serif !important;
}

div.featureditem {
	font-family: "DejaVu LGC Sans", "DejaVu Sans", sans-serif !important;
}

h1 {
	font-family: "DejaVu LGC Serif", "DejaVu Sans", sans-serif !important;
}

div.articlebody p {
	font-family: "DejaVu LGC Sans", "DejaVu Sans", sans-serif !important;
}

div.articlebody a{
	font-family: "DejaVu LGC Sans", "DejaVu Sans", sans-serif !important;
}
	

div.mainheadline {
	font-family: "DejaVu LGC Sans", "DejaVu Sans", sans-serif !important;
}

div.newsletter {
    font-family: "DejaVu LGC Sans Mono", "DejaVu Sans Mono", monospace !important;
}


font {
    font-family: inherit !important;
}



}
