css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://home.myspace.com/) {

body{
background-color: black !important;
}

.heading, #topnav {
background-color: #600 !important;
}

a:link, a:visited {
color: #A50000 !important;
text-decoration: underline !important;
}

a:hover {
color: #A50000 !important;
text-decoration: none !important;
}

#topnav a:link, #topnav a:visited {
color: white !important;
}

#splash_coolNewPeople, #home_featured_music, #home_featured_filmmaker, #header, #home_greybox, #home_setHomePage, #squareAd, .googleafc, iframe {
display: none !important;
width: 0 !important;
height: 0 !important;
}

.left, .right:link {
color: white !important;
}

.section {
border-color: #600 !important;
background-color: white !important;
}

#wrap, td, #footer {
background-color: #ECECEC !important;
}

th {
background-color: #A50000 !important;
color: white !important;
padding: 5px 0 5px 5px;
}

.mar5 {
color: black !important;
}

div #home_profileInfo td {
background-color: white !important;
}

#home_profileInfo img {
margin-left: 15px;
}

#home_infoBar {
width: 432px;
margin: 0;
}

#main {
margin-top: 10px !important;
height: 1000px !important;
overflow: auto !important;
}

}
