css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(boards.straightdope.com){
td,th,p,li,.navbar,.smallfont>STRONG{font-size:16px !important;}
BODY>DIV{margin-top:-30px !important;}
BODY>CENTER,BODY>.borders,#secondarea,
BODY>CENTER+TABLE,#thirdarea,
#google_ads_frame1,.tborder+BR,.vbmenu_control,
#pagenav_menu+.tborder,.alt2>FORM,iframe,
IMG[src$="/quote.gif"],IMG[src$="/reply.gif"],
IMG[src$="/newthread.gif"],#posts>DIV:first-child+DIV,
DIV[style="padding: 0px 0px 6px; background-color: rgb(51, 0, 153);"],
DIV[style="background-color: rgb(51, 0, 153); padding: 0px 0px 6px;"]
{display:none !important;}}
