css:
@-moz-document url-prefix("http://www.amazon."), url-prefix("https://www.amazon.") {
#popc {
overflow: hidden !important;
}
#popc img {
margin: 6px;
padding: 4px;
background: #fff;
border: 1px solid #ccc;
border-radius: 4px;
-moz-border-radius: 4px;
box-shadow: 2px 2px 6px #777;
-moz-box-shadow: 2px 2px 6px #777;
}
}
