css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("asphalto.org") {

img {max-width:450px;}

img:hover {max-width:100%;}

div.signature img {max-width:800px; max-height:50px;}

.signature * {font-size: 1em !important;}

.signature:hover, .signature img:hover, .signature img:hover {max-width:100%; max-height:100%; height:100%; overflow:auto;}

.signature {max-height:100px; overflow:hidden;}

}
