css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("live.com"){
   #AdContainer, div[id^="RadAd_"]{
      display: none !important;
   }
   #header div.stroke {
      display: none;
   }
}
@-moz-document domain("spaces.live.com"){
   div.SearchSection {
      visibility: hidden;
   }
   .uxp_hdr_hideTabs {
      display: none;
   }
   .sn_lbar_bkgnd {
      display: none;
   }
   .sn_lbar_bkgnd_shadow {
      display: none;
   }
}
@-moz-document domain("mail.live.com"){
   .Managed .WithSkyscraper #MainContent {
      right: 4px !important;
   }
   .Unmanaged .WithSkyscraper #MainContent {
      margin-right: 4px !important;
   }
}
@-moz-document domain("skydrive.live.com") {
   tr.adRow {
      display: none;
   }
}
