css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("channelnewsasia.com")
{
	/* Remove banner ad space. */
	table[width="730"][border="0"][height="90"][cellpadding="1"][cellspacing="0"],

	/* Remove header space in news article. */
	td[width="221"][rowspan="3"], td[width="728"][height="45"], td[height="90"][valign="top"],
	td[width="160"][rowspan="2"][valign="top"],

	/* Hide blue right sidebar. */
	td[width="130"][bgcolor="#006699"][valign="top"],

	/* Hide footer. */
	img[src="shim.gif"],
	table[border="0"][cellpadding="0"][cellspacing="0"][width="967"]>tbody>tr>td[bgcolor="#ffffff"],
	table[border="0"][cellpadding="0"][cellspacing="0"][width="967"]>tbody>tr>td>table[border="0"][cellpadding="0"][cellspacing="0"][width="596"],
	#copyright { display: none !important; }

	table[width="789"] { width: 937px !important; }
	table[width="784"], td[width="784"] { width: 932px !important; }
}
