css:
@-moz-document domain('engadget.com') {

body, .caption, .post_show_tags, .clearfix
{
	font-family:Arial, Helvetica, sans-serif !important;
	font-style:normal !important;
}

.comment_date
{
	font-family:Arial, Helvetica, sans-serif !important;
	font-style:normal !important;
	color: #B3B3B3;
}
.comment_date a
{
	font-style: bold !important;
	color: #78C0D5 !important;
}
.comment_date a:hover
{
	color: #54E0FE !important;
}

.comment_entry
{
	background-color: #E5F9FF !important;
		border-style:dotted solid !important;
	border-color: #78C0D5 !important;

}


/* VOTE - Work in progress */
/*
.comment_plus, .voteLink
{
	background-color: #AAD3A7 !important;
}
*/

a.caption
{
	font-family:Arial, Helvetica, sans-serif !important;
	font-size: 70% !important;
}

.post_body, .post_info, .mc_heading, .mc_blurb {
	font-family:Arial, Helvetica, sans-serif !important;
	font-size: 85%;
}

			 
a.post_body , h4.post_title a{
	color: #0099CC !important;
}

a.post_body:hover , h4.post_title a:hover{
	color: #FFFFFF !important;
	background-color: #0099CC !important;
}


.post_body
 {
	color: #5A5A5A !important;
}


/* some customization for comments */
#comments div {
border: none !important;
color: #626262 !important;
}




/* hello and goodbye ads :D */

#outerslice, .sponsorArea, .top_content, .topstories, .img_holder, .bottom_content, .grid_holder
{
	display: none !important;
	max-height: 1px !important;
	height: 1px !important;
}

.my-little-hero {
		  display:none!important;
}

}
