css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.tppc.info") {

#brdmenu {position:absolute; top:116px; left:170px; right:170px; background-color:#383838 !important;}



#brdtitle {display:none}

#brdwelcome {
padding-top:126px !important; 
background: #383838 url('http://img94.imageshack.us/img94/487/tcheader.png') no-repeat center top; color:#d4d4d4; overflow:hidden;}

div.ad {display:none;}

body {padding-left:10% !important;
padding-right:10% !important;
background-image:url('http://img34.imageshack.us/img34/6540/1250704394244.jpg'); background-repeat:repeat;
background-attachment:fixed;
}

#brdtitle {height:100px; overflow:hidden;}

tr.iclosed a {color:#888 !important;}

A:link {color: #fc62c0 !important; text-decoration: none}
A:visited {color: #fc62c0 !important; text-decoration: none}
A:active {color: #fc62c0 !important; text-decoration: none}
A:hover {color: #ffa0da !important; text-decoration: none}

legend {color:#fc62c0 !important;}

th.tc2, th.tc3 {width:0px !important;}
th.tcr {width:20% !important;}


.h2 {background-color:#2A2A2A; background-attachment:fixed;}

}
